mapper.select:
SELECT * FROM custom_order

	AND del = 0
	AND id=#{id}
	AND number=#{number}
	AND tableNo=#{tableNo}
	AND isChecked=#{isChecked}

		AND createTime BETWEEN #{startTime} AND #{endTime}
		AND createTime >= #{startTime}


			AND createTime <= #{endTime}





	ORDER BY

		${sort.field} ${sort.dir}



	LIMIT #{start},#{size}

SELECT COUNT(*) FROM custom_order

	AND del = 0
	AND id=#{id}
	AND number=#{number}
	AND tableNo=#{tableNo}
	AND isChecked=#{isChecked}

		AND createTime BETWEEN #{startTime} AND #{endTime}
		AND createTime >= #{startTime}


			AND createTime <= #{endTime}

mapper.update:
UPDATE custom_order
createTime = #{createTime}, receiveTime = #{receiveTime}, receiver = #{receiver}, completeTime = #{completeTime}, completeOperator = #{completeOperator}, totalPrice = #{totalPrice}, actualPrice = #{actualPrice}, isChecked = #{isChecked}, checkTime = #{checkTime}, checkOperator = #{checkOperator}, pushed = #{pushed}, rush = #{rush},
WHERE id = #{id}

UPDATE custom_order
SET del = 1
WHERE number = #{number}
