mapper.select:
SELECT * FROM food_supply

	AND id=#{id}
	AND supplyDate=#{supplyDate}
	AND menuId=#{menuId}
	AND operator=#{operator}



	LIMIT #{start},#{size}

mapper.update:
UPDATE food_supply
menuId = #{menuId}, mount = #{mount}, unit = #{unit}, left = #{left}, supplyDate = #{supplyDate}, operator = #{operator},
WHERE id = #{id}
