mapper.select:
SELECT t1.*, t2.name AS categoryName, t3.cname AS unitCname, t3.ename AS unitEname
FROM material t1
LEFT JOIN material_category t2 ON t2.id = t1.categoryId
LEFT JOIN measurement t3 ON t3.id = t1.unit

	AND t1.id=#{id}
	AND t1.categoryId=#{categoryId}

		AND updateTime BETWEEN #{startTime} AND #{endTime}
		AND updateTime >= #{startTime}


			AND updateTime <= #{endTime}





	ORDER BY

		${sort.field} ${sort.dir}



	LIMIT #{start},#{size}

SELECT COUNT(*)
FROM material

	AND categoryId=#{categoryId}

		AND updateTime BETWEEN #{startTime} AND #{endTime}
		AND updateTime >= #{startTime}


			AND updateTime <= #{endTime}

mapper.delete:
DELETE FROM material
WHERE id IN

	#{id}
